mapper.select:

select count(1)
    from tb_device dv
    left join tb_hospital hs on dv.hospital_id = hs.hospital_id
    left join tb_department dm on dv.dept_id = dm.dept_id



            dv.device_id = #{deviceId}


            and dv.device_code like #{deviceCode}


            and dv.device_name like #{deviceName}


            and hs.hospital_name like #{hospital}


            and dm.dept_name like #{department}


            and dv.asset_no like #{assetNo}


            and dv.device_model like #{deviceModel}


            and dv.device_state = #{deviceState}


            and dv.device_type = #{deviceType}


            and dv.usage_state = #{usageState}


            and dv.manufacturer like #{manufacturer}


            and dv.producing_place like #{producingPlace}


            and dv.device_owner like #{deviceOwner}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.device_desc, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.qr_code, dv.manufacturer, dv.producing_place, dv.setup_date, dv.accept_date, dv.accessory, dv.accept_remark, dv.accept_file, dv.use_date, dv.device_owner, dv.storage_date, dv.purchase_amount, dv.maintenance_end_date, dv.warranty_begin_date, dv.warranty_end_date, dv.warranty_amount, dv.warranty_content, dv.sales_supplier, dv.sales_supplier_contact, dv.sales_supplier_phone, dv.sales_supplier_desc, dv.after_sale_provider, dv.after_sale_provider_engineer, dv.after_sale_provider_phone, dv.after_sale_provider_desc, dv.contract_id, dv.create_time, dv.creater, dv.modify_time, dv.modifier
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
dv.device_id = #{deviceId}
and dv.device_code like #{deviceCode}
and dv.device_name like #{deviceName}
and hs.hospital_name like #{hospital}
and dm.dept_name like #{department}
and dv.asset_no like #{assetNo}
and dv.device_model like #{deviceModel}
and dv.device_state = #{deviceState}
and dv.device_type = #{deviceType}
and dv.usage_state = #{usageState}
and dv.manufacturer like #{manufacturer}
and dv.producing_place like #{producingPlace}
and dv.device_owner like #{deviceOwner}
order by dv.device_id limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.picture1, dv.picture2, dv.picture3, dv.picture4, dv.picture5, dv.device_desc, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.qr_code, dv.asset_no, dv.manufacturer, dv.producing_place, dv.setup_date, dv.accept_date, dv.accessory, dv.accept_remark, dv.accept_file, dv.use_date, dv.device_owner, dv.storage_date, dv.purchase_amount, dv.need_inspection, dv.inspection_interval, dv.next_inspection_date, dv.need_maintain, dv.maintenance_interval, dv.next_maintenance_date, dv.need_metering, dv.metering_interval, dv.next_metering_date, dv.warranty_begin_date, dv.warranty_end_date, dv.warranty_amount, dv.warranty_content, dv.sales_supplier, dv.sales_supplier_contact, dv.sales_supplier_phone, dv.sales_supplier_desc, dv.after_sale_provider, dv.after_sale_provider_engineer, dv.after_sale_provider_phone, dv.after_sale_provider_desc, dv.contract_id, dv.create_time, dv.creater, dv.modify_time, dv.modifier
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
where dv.device_id = #{deviceId}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.setup_date
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
where dv.device_id = #{deviceId}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.picture1, dv.picture2, dv.picture3, dv.picture4, dv.picture5, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.qr_code, dv.manufacturer, dv.producing_place, dv.setup_date, dv.accept_date, dv.accessory, dv.accept_remark, dv.accept_file, dv.use_date, dv.device_owner, dv.storage_date, dv.purchase_amount, dv.maintenance_end_date, dv.warranty_begin_date, dv.warranty_end_date, dv.warranty_amount, dv.warranty_content, dv.sales_supplier, dv.sales_supplier_contact, dv.sales_supplier_phone, dv.sales_supplier_desc, dv.inspection_interval, dv.next_inspection_date
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
where dv.need_inspection = 1 and dv.inspection_owner = #{assigneeUserId}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.picture1, dv.picture2, dv.picture3, dv.picture4, dv.picture5, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.qr_code, dv.manufacturer, dv.producing_place, dv.setup_date, dv.accept_date, dv.accessory, dv.accept_remark, dv.accept_file, dv.use_date, dv.device_owner, dv.storage_date, dv.purchase_amount, dv.maintenance_end_date, dv.warranty_begin_date, dv.warranty_end_date, dv.warranty_amount, dv.warranty_content, dv.sales_supplier, dv.sales_supplier_contact, dv.sales_supplier_phone, dv.sales_supplier_desc, dv.maintenance_interval, dv.next_maintenance_date
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
where dv.need_maintain = 1 and to_char(next_maintenance_date, 'YYYYMM') = #{nextMaintainTime}

select dv.device_id, dv.device_code, dv.device_name, dv.hospital_id, hs.hospital_name, dv.dept_id, dm.dept_name, dv.picture1, dv.picture2, dv.picture3, dv.picture4, dv.picture5, dv.device_model, dv.device_state, dv.device_type, dv.serial_number, dv.usage_state, dv.qr_code, dv.manufacturer, dv.producing_place, dv.setup_date, dv.accept_date, dv.accessory, dv.accept_remark, dv.accept_file, dv.use_date, dv.device_owner, dv.storage_date, dv.purchase_amount, dv.maintenance_end_date, dv.warranty_begin_date, dv.warranty_end_date, dv.warranty_amount, dv.warranty_content, dv.sales_supplier, dv.sales_supplier_contact, dv.sales_supplier_phone, dv.sales_supplier_desc, dv.metering_interval, dv.next_metering_date
from tb_device dv left join tb_hospital hs on dv.hospital_id = hs.hospital_id left join tb_department dm on dv.dept_id = dm.dept_id
where dv.need_metering = 1 and to_char(next_metering_date, 'YYYYMMDD') = #{nextMeteringTime}

mapper.update:
update tb_device 
  set
    usage_state = #{usageState}
  where setup_case_id = #{setupCaseId,jdbcType=INTEGER}

update tb_device 
  set
    next_inspection_date =#{nextInspectionDate,jdbcType=TIMESTAMP}
  where device_id = #{deviceId,jdbcType=INTEGER}

update tb_device 
  set
    next_metering_date =#{nextMeteringDate,jdbcType=TIMESTAMP}
  where device_id = #{deviceId,jdbcType=INTEGER}

update tb_device 
  set
    next_maintenance_date =#{nextMaintenanceDate,jdbcType=TIMESTAMP}
  where device_id = #{deviceId,jdbcType=INTEGER}
