mapper.select:
select count(1)
  from tb_setup_case as tsc
    left join tb_device as td
        on td.setup_case_id = tsc.case_id 


        tsc.assignee_user_id = #{assigneeUserId}


        tsc.case_state = #{caseState}


        and td.device_code like #{deviceCode}


        and td.device_name like #{deviceName}

select tmc.*,td.device_id,th.hospital_name,tdm.dept_name
      from tb_setup_case as tmc
        left join tb_device as td
            on td.setup_case_id = tmc.case_id 
        left join tb_hospital as th
            on tmc.hospital_id = th.hospital_id
        left join tb_department as tdm
            on tmc.dept_id = tdm.dept_id


            tmc.assignee_user_id = #{assigneeUserId}


            tmc.case_state = #{caseState}


            and td.device_code like #{deviceCode}


            and td.device_name like #{deviceName}


     order by tmc.create_time desc
     limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

select count(1)
    from tb_setup_case 
        where assignee_user_id = #{assigneeUserId}
