mapper.select:
select count(1)
  from tb_inspection_case as tsc
    left join tb_device as td
        on td.device_id = tsc.device_id


        and tsc.assignee_user_id = #{assigneeUserId}


        and td.device_code like #{deviceCode}


        and td.device_name like #{deviceName}

select tmc.*, td.device_name, tu.display_name, th.hospital_name,tdm.dept_name from tb_inspection_case as tmc left join tb_device as td on td.device_id = tmc.device_id left join tb_hospital as th on td.hospital_id = th.hospital_id left join tb_department as tdm on td.dept_id = tdm.dept_id left join tb_user as tu on tmc.assignee_user_id = tu.user_id
tmc.assignee_user_id = #{assigneeUserId}
and td.device_code like #{deviceCode}
and td.device_name like #{deviceName}
order by tmc.create_time desc limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

mapper.update:
    update tb_inspection_case 


                case_remark = #{caseRemark},


                case_state = #{caseState},


                assignee_user_id = #{assigneeUserId},


                setup_time = to_timestamp(#{setupTime},'YYYY-MM-DD HH24:MI:SS'),


                setup_remark = #{setupRemark},


	           modify_time = #{modifyTime,jdbcType=TIMESTAMP},
	           modifier = #{modifier}



               case_id = #{caseId}


               device_id = #{deviceId}
