mapper.select:
select count(1)
  from tb_pm_case as tsc
    left join tb_device as td
        on td.device_id = tsc.device_id


        tsc.assignee_user_id = #{assigneeUserId}


        and td.device_code like #{deviceCode}


        and td.device_name like #{deviceName}

select tmc.*,td.device_name, tu.display_name as assignee_user_name, tu1.display_name as actual_user_name, th.hospital_name,tdm.dept_name from tb_pm_case as tmc left join tb_device as td on td.device_id = tmc.device_id left join tb_hospital as th on td.hospital_id = th.hospital_id left join tb_department as tdm on td.dept_id = tdm.dept_id left join tb_user as tu on tmc.assignee_user_id = tu.user_id left join tb_user as tu1 on tmc.actual_pm_user_id = tu1.user_id
tmc.assignee_user_id = #{assigneeUserId}
and td.device_code like #{deviceCode}
and td.device_name like #{deviceName}
order by tmc.create_time desc limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

SELECT case_id from tb_pm_case
  where device_id = #{deviceId,jdbcType=INTEGER}
       and case_state = 10
