mapper.select:
select count(1) from ( select tmc.*, string_agg (tde.dept_name, ',' ) as dept_name_arr, tu.display_name from tb_stocktaking_case as tmc inner join tb_stocktaking_case_plan_dept as tscp on tmc.case_id = tscp.case_id inner join tb_department as tde on tde.dept_id = tscp.dept_id left join tb_user as tu on tmc.assignee_user_id = tu.user_id
tmc.assignee_user_id = #{assigneeUserId}
and tmc.case_state = #{caseState}
and tmc.plan_begin_time >= to_timestamp(#{planBeginTimeFrom},'YYYY-MM-DD HH24:MI:SS')
and tmc.plan_begin_time <= to_timestamp(#{planBeginTimeTo},'YYYY-MM-DD HH24:MI:SS')
group by tmc.case_id,tu.display_name order by tmc.case_id, tmc.create_time desc ) as t

select tmc.*, string_agg (tde.dept_name, ',' ) as dept_name_arr, tu.display_name from tb_stocktaking_case as tmc inner join tb_stocktaking_case_plan_dept as tscp on tmc.case_id = tscp.case_id inner join tb_department as tde on tde.dept_id = tscp.dept_id left join tb_user as tu on tmc.assignee_user_id = tu.user_id
tmc.assignee_user_id = #{assigneeUserId}
and tmc.case_state = #{caseState}
and tmc.plan_begin_time >= to_timestamp(#{planBeginTimeFrom},'YYYY-MM-DD HH24:MI:SS')
and tmc.plan_begin_time <= to_timestamp(#{planBeginTimeTo},'YYYY-MM-DD HH24:MI:SS')
group by tmc.case_id,tu.display_name order by tmc.case_id, tmc.create_time desc limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

select tsd.*, tu.display_name,td.device_name,tde.dept_name from tb_stocktaking_case_actual_device as tsd inner join tb_stocktaking_case as tsc on tsd.case_id = tsc.case_id left join tb_user as tu on tsd.operation_user_id = tu.user_id inner join tb_device as td on tsd.device_id = td.device_id inner join tb_department as tde on tde.dept_id = td.dept_id
tsd.case_id = #{caseId}
tsd.operation_user_id = #{operationUserId}
tsc.assignee_user_id = #{assigneeUserId}
order by td.dept_id, tsd.operation_time desc limit #{pageSize,jdbcType=INTEGER} offset #{pageIndex,jdbcType=INTEGER}

select tmc.*, string_agg (tde.dept_name, ',' ) as dept_name_arr, tu.display_name from tb_stocktaking_case as tmc inner join tb_stocktaking_case_plan_dept as tscp on tmc.case_id = tscp.case_id inner join tb_department as tde on tde.dept_id = tscp.dept_id left join tb_user as tu on tmc.assignee_user_id = tu.user_id
tmc.case_id = #{caseId}
group by tmc.case_id,tu.display_name

